mapper.select:
select
    if(count(address_no) = 0, true, false) as isAddressAuth
  from address
 where partner_code = #{partnerCode}
   and address_no = #{addressNo}

mapper.update:
update address
   set rep_unstoring_flag = 'N'
 where partner_code = #{partnerCode}

update address
   set rep_return_flag = 'N'
 where partner_code = #{partnerCode}
