mapper.select:
select
    if(count(order_no) = 0, true, false) as isOrderAuth
  from order_info
 where partner_code = #{partnerCode}
   and order_no = #{orderNo}

select count(1) as totalOrderCount, -- 전체주문 건수 ifnull(sum(if(o.order_status_code = 'O05', 1, 0)), 0) as completeOrderCount, -- 주문완료 건수 ifnull(sum(if(o.order_status_code = 'D01', 1, 0)), 0) as readyProductCount, -- 상품준비 건수 ifnull(sum(if(o.order_status_code = 'O06', 1, 0)), 0) as requestCancelCount, -- 취소요청 건수 ifnull(sum(if(o.order_status_code = 'C05', 1, 0)), 0) as confirmPurchaseCount, -- 구매확정 건수 ifnull(sum(if(o.order_status_code = 'R05', 1, 0)), 0) as completeReturnCount -- 반품완료 건수 from order_info o inner join order_payment p on o.order_no = p.order_no where o.partner_code = #{partnerCode}
o.order_no like #{item} '%'
o.order_name like #{item} '%'
o.order_user_name like #{item} '%'
and p.payment_date between concat(#{startDay}, ' 00:00:00.0') and concat(#{endDay}, ' 23:59:59.9')
and p.cancel_date between concat(#{startDay}, ' 00:00:00.0') and concat(#{endDay}, ' 23:59:59.9')
-- 주문 상태
#{item}
