mapper.select:
select o.order_no, date_format(o.order_date, '%Y-%m-%d') as order_day, o.order_user_name, o.order_status_code, p.payment_method_code, o.order_name, p.order_amount, p.ssp_point, p.sales_amount, p.payment_amount, date_format(p.payment_date, '%Y-%m-%d') as payment_day, date_format(p.cancel_date, '%Y-%m-%d') as cancel_day, p.payment_status_code from order_info o inner join order_payment p on o.order_no = p.order_no where o.partner_code = #{partnerCode}
o.order_no like #{item} '%'
o.order_name like #{item} '%'
o.order_user_name like #{item} '%'
and p.payment_date between concat(#{startDay}, ' 00:00:00.0') and concat(#{endDay}, ' 23:59:59.9')
and p.cancel_date between concat(#{startDay}, ' 00:00:00.0') and concat(#{endDay}, ' 23:59:59.9')
-- 주문 상태
#{item}
-- 결제 상태
#{item}
-- 결제 방법
#{item}
order by o.create_date desc

select count(1) as totalOrderCount, -- 전체주문 건수 sum(if(p.payment_status_code = 'O05', 1, 0)) as completeOrderCount, -- 주문완료 건수 sum(if(p.payment_status_code = 'D01', 1, 0)) as readyProductCount, -- 상품준비 건수 sum(if(p.payment_status_code = 'O06', 1, 0)) as requestCancelCount, -- 취소요청 건수 sum(if(p.payment_status_code = 'C05', 1, 0)) as confirmPurchaseCount, -- 구매확정 건수 sum(if(p.payment_status_code = 'R05', 1, 0)) as completeReturnCount -- 반품완료 건수 from order_info o inner join order_payment p on o.order_no = p.order_no where o.partner_code = #{partnerCode}
o.order_no like #{item} '%'
o.order_name like #{item} '%'
o.order_user_name like #{item} '%'
and p.payment_date between concat(#{startDay}, ' 00:00:00.0') and concat(#{endDay}, ' 23:59:59.9')
and p.cancel_date between concat(#{startDay}, ' 00:00:00.0') and concat(#{endDay}, ' 23:59:59.9')
-- 주문 상태
