mapper.select:
-- 결제 취소 / 취소 요청 상태 확인
select
    if(pay_stus in ('S08', 'S09'), true, false) as isCancelPayment
  from ord_prnr_info
 where ord_no = #{orderNo}
   and prnr_cd = #{partnerCode}

-- 상품 준비중 상태인지 여부 확인
select
    if(ord_stus in ('O05', 'D01'), false, true) as isNonReadyProduct
  from ord_prnr_info
 where ord_no = #{orderNo}
   and prnr_cd = #{partnerCode}

mapper.update:
-- 결제 취소요청 update ord_prnr_info set pay_stus = 'S09', -- 취소요청 코드 cnc_dt = now(),
cnc_rsn_cd = #{cancelReasonCode},
upd_usr_id = #{userId}, upd_dt = now() where ord_no = #{orderNo} and prnr_cd = #{partnerCode}

mapper.select:
-- 부분취소를 지원 안하는 관계로 등록되어있는 상품 수와 취소 요청 상품 수 일치여부 확인
select
	if(count(ord_prdt_idx) != ${orderProductSeq.length}, true, false) as isPartialPaymentCancel
  from ord_prdt_info
 where ord_no = #{orderNo}
   and prnr_cd = #{partnerCode}
