mapper.select:
select
    if(count(ord_no) = 1, true, false) as isRequestReturn
  from ord_prnr_info
 where ord_no = #{orderNo}
   and prnr_cd = #{partnerCode}
   and ord_stus = #{returnProcessCode}

mapper.update:
update ord_prnr_info
   set rtrn_memo = #{returnMemo},
	   upd_usr_id = #{userId},
	   upd_dt = now()
 where ord_no = #{orderNo}
   and prnr_cd = #{partnerCode}

mapper.select:
select
    ord_prdt_stus as order_product_status_code
  from ord_prdt_info order_product
 where ord_no = #{orderNo}
   and prnr_cd = #{partnerCode}
   and ord_prdt_idx = #{orderProductSeq}

mapper.update:
update ord_prdt_info set ord_prdt_stus = #{returnProcessCode}, -- 반풀불가 처리 시 반품 사유 등록
non_rtn_tp_cd = #{requestCode}, non_rtn_dt = now(),
upd_usr_id = #{userId}, upd_dt = now() where ord_no = #{orderNo} and ord_prdt_idx = #{orderProductSeq}

update ord_prnr_info
   set ord_stus = #{returnProcessCode},
       upd_usr_id = #{userId},
       upd_dt = now()
 where ord_no = #{orderNo}
   and prnr_cd = #{partnerCode}

mapper.select:
select count(*) over (partition by o.ord_no) as order_product_count, count(*) over (partition by o.ord_no, p.prdt_cd) as product_count, date_format(p.cnc_rq_dt, '%Y-%m-%d') as request_return_day, o.ord_no as order_no, date_format(o.ord_dt, '%Y-%m-%d') as order_day, s.ord_nm as order_name, p.prdt_cd as product_code, p.prdt_nm as product_name, if(p.optn_nm is null, '', concat(p.optn_nm, '-', p.optn_val)) as product_option, p.prdt_qty as product_qty, p.fnl_sell_amt as product_sell_amount, p.fnl_sell_amt as product_amount, p.dlvr_chrg_amt as product_delivery_charge_amount, s.pay_amt as payment_amount, m.usr_nm as order_user_name, p.dlvr_tp as delivery_type, p.cnc_cplt_dt as complete_return_day, p.ord_prdt_stus as return_status, ifnull(c.intg_cd_val_nm, '') as delivery_company, ifnull(d.rtrn_invc_no, '') as invoice_no, d.rtrn_rcpnt_nm as recipient_name, d.rtrn_rcpnt_usr_phn as recipient_user_phone, ifnull(concat('(', d.rtrn_rcpnt_adrs_zip_cd, ') ', d.rtrn_rcpnt_adrs), '') as recipient_address, d.rtrn_rcpnt_adrs_sub as recipient_address_detail, ifnull(r.intg_cd_val_nm, '') as reason from ord_info o inner join ord_prnr_info s on o.ord_no = s.ord_no and s.prnr_cd = #{partnerCode} inner join ord_prdt_info p on o.ord_no = p.ord_no left join ord_shpn_info d on p.ord_no = d.ord_no and p.ord_prdt_idx = d.ord_prdt_idx left join com_mbr_info m on o.ord_usr_id = m.usr_id left join com_intg_cd_dtl c on d.rtrn_dlvry_cmpny = c.intg_cd_val and c.intg_cd_id = '000044' left join com_intg_cd_dtl r on p.non_rtn_tp_cd = r.intg_cd_val and r.intg_cd_id = '000085' where o.ord_no in
#{item}
order by o.ord_no desc, p.prdt_cd
