mapper.update:
update partner_invite set
    invite_status = #{inviteStatus}
where invite_code = #{inviteCode}

update partner set
	thumbnail = #{thumbnail}
where partner_code = #{partnerCode}

mapper.select:
select if(count(partner_code) = 0, false, true) as isPartnerAuth
from users_partner
where user_id = #{userId}
and auth_code = #{authCode}
and partner_code = #{partnerCode}

select if(count(partner_code) = 3, false, true) as isPartnerOperatorCount
from users_partner
where auth_code = #{authCode}
  and partner_code = #{partnerCode}

select if(count(user_id) = 0, true, false) as isPartnerOperatorCount
from users_partner
where auth_code = #{authCode}
  and partner_code = #{partnerCode}
  and user_id = #{userId}

mapper.update:
update partner set
	partner_name = #{partnerName}
,	business_classification_code = #{businessClassificationCode}
,	business_name = #{businessName}
,	business_no = #{businessNo}
,	business_condition = #{businessCondition}
,	business_type = #{businessType}
,	rep_name = #{repName}
,	rep_phone = #{repPhone}
,	cs_phone = #{csPhone}
,	business_address = #{businessAddress}
,	business_address_sub = #{businessAddressSub}
,	business_address_zip_code = #{businessAddressZipCode}
,	update_user_id = #{userId}
,	update_date = current_timestamp
where partner_code = #{partnerCode}

update partner_contract set
    contract_manager_name = #{contractManagerName}
,	contract_manager_phone = #{contractManagerPhone}
	where partner_code = #{partnerCode}
	and contract_code = #{contractCode}

update partner_contract set
	contract_manager_name = #{contractManagerName}
  ,	contract_manager_phone = #{contractManagerPhone}
  ,	account_no = #{accountNo}
  ,	account_holder = #{accountHolder}
  ,	bank_code = #{bankCode}
  ,	tax_bill_email = #{taxBillEmail}
where partner_code = #{partnerCode}
  and contract_code = #{contractCode}

mapper.select:
select pn.notification_phone
from partner_notification pn
where pn.partner_code = #{partnerCode}

mapper.insert:
insert into partner
    (
     partner_code
    , partner_name
    , partner_status
    , partner_kind
    , business_classification_code
    , business_name
    , business_no
    , business_condition
    , business_type
    , rep_name
    , rep_phone
    , cs_phone
    , business_address
    , business_address_sub
	, business_address_zip_code
    , create_user_id
    , update_user_id
    )
values
	(
	 #{partnerCode}
	, #{partnerName}
	, #{partnerStatus}
	, #{partnerKind}
	, #{businessClassificationCode}
	, #{businessName}
	, #{businessNo}
	, #{businessCondition}
	, #{businessType}
	, #{repName}
	, #{repPhone}
	, #{csPhone}
	, #{businessAddress}
	, #{businessAddressSub}
	, #{businessAddressZipCode}
	, #{userId}
	, #{userId}
	)

insert into partner_contract
    (
     partner_code
     , contract_code
     , contract_status
     , contract_manager_name
     , contract_manager_phone
     , tax_bill_email
     , bank_code
     , account_no
     , account_holder
     , contract_day
     , contract_start_day
     , contract_end_day
     , contract_file_no
     , create_user_id
     , update_user_id
     )
values
	(
	#{partnerCode}
	, #{contractCode}
	, #{contractStatus}
	, #{contractManagerName}
	, #{contractManagerPhone}
	, #{taxBillEmail}
	, #{bankCode}
	, #{accountNo}
	, #{accountHolder}
	, #{contractDay}
	, #{contractStartDay}
	, #{contractEndDay}
	, #{contractFileNo}
	, #{userId}
	, #{userId}
	)

insert into partner_invite
    (invite_code
    , partner_code
    , auth_code
    , invite_status
    , invite_receiver
    , invite_sender
    , create_user_id
    )
VALUES
	(
	 #{inviteCode}
	, #{partnerCode}
	, #{authCode}
	, #{inviteStatus}
	, #{inviteReceiver}
	, #{inviteSender}
	, #{userId}
	)
