mapper.select:
select
    if(count(template_no) = 0, true, false) as isTemplateAuth
  from template
 where partner_code = #{partnerCode}
   and template_no = #{templateNo}

select template_no, template_type, template_name, create_date from template where partner_code = #{partnerCode} -- 템플릿 구분
#{item}
order by create_date desc
