mapper.select:
select
    user_id
,	user_pwd
,	user_name
  from users
 where 1=1

   and user_id = #{userId}


   and user_name = #{userName}


   and user_phone = #{userPhone}

mapper.update:
update users
	set
	user_pwd = #{userPwd}
,	change_pwd_flag = 1
where user_id = #{userId}

mapper.insert:
insert into users
	(user_id
	, user_pwd
	, user_name
	, user_phone
	, user_status_code
	, terms_agreed_flag
	, collection_agreed_flag
	, promotion_agreed_flag
	, login_fail_count
	, create_user_id
	, update_user_id
	)
values
	(#{userId}
	, #{userPwd}
	, #{userName}
	, #{userPhone}
	, #{userStatusCode}
	, #{termsAgreedFlag}
	, #{collectionAgreedFlag}
	, #{promotionAgreedFlag}
	, 0
	, #{userId}
	, #{userId}
	)

insert into users_partner
	(user_id
	, partner_code
	, auth_code
	, create_user_id
	, update_user_id
	)
values
	(#{userId}
	, #{partnerCode}
	, #{authCode}
	, #{userId}
	, #{userId}
	)
